<?php	class EmailData	{		var $name;		var $email;		var $msg;				public function getName() { return $this->name; }		public function getEmail() { return $this->email; }		public function getMsg() { return $this->msg; }				public function loadData($name,$email,$msg)		{			$this->name = $name;			$this->email = $email;			$this->msg = $msg;		}				public function verifyName()		{			$lenght = strlen($this->name);			if($lenght < 7)			{				return false;			}								if( !(strstr($this->name," ")) )			{				return false;			}						return true;		}				public function verifyEmail()		{			if( !(strstr($this->email,"@")) && !(strstr($this->email,".")) )			{				return false;			}						return true;		}				public function verifyMsg()		{			$lenght = strlen($this->msg);			if($lenght < 10)			{				return false;			}						return true;		}				public function sendEmail()		{			//enviando email		}		}	?>